INSERT INTO `research_enroll` VALUES (1282, '2020-11-03 18:10:40', '2006190095', '尹旭', '基础俄语教学过程中历时阐释的活用——基于网络教学平台的微课体系构建设想', '/upload/xinsilu/researchEnroll/2020/2006190095-尹旭-教研论文.zip', '尹旭，男，1990.3，博士，北京大学外国语学院俄语系助理教授；主要成果：在中、俄期刊发表论文8篇，参与中、俄科研项目3个；研究方向：俄罗斯语法思想史、俄语标准语史、对外俄语教学中的俄语历时知识应用；通讯地址：北京市海淀区颐和园路5号北京大学外国语学院新楼526，电话：13426303102，邮箱：yinxuxy@pku.edu.cn', NULL, '教研论文', NULL, NULL, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `research_enroll` VALUES (1280, '2020-11-03 11:19:54', '2011110143；73048（通讯作者）', '王斯悦；吴涛（通讯作者）', '翻转课堂混合教学模式提高流行病学学习效果的研究', '/upload/xinsilu/researchEnroll/2020/2011110143；73048（通讯作者）-王斯悦；吴涛（通讯作者）-教研论文.zip', '吴涛，女，1973年9月生，博士，北京大学公共卫生学院教授。研究方向为遗传流行病学；以责任作者或第一作者身份发表中英文学术论文九十多篇。作为负责人建设了两门国家精品在线课程《流行病学基础》（一）、（二），发表多篇国内外教学论文。通讯地址：北京大学医学部公共卫生学院，联系电话13910033458，email：twu@bjmu.edu.cn', NULL, '教研论文', NULL, NULL, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
