INSERT INTO `research_enroll` VALUES (2247, '2022-12-29 19:02:06', '0006172220', '蔡云凌', '“中文作为第二语言教学法”课程教学模式的创新和实践', '/upload/xinsilu/researchEnroll/2023/项目申报/0006172220-蔡云凌/项目申报书_蔡云凌_对外汉语教育学院.docx.doc;/upload/xinsilu/researchEnroll/2023/项目申报/0006172220-蔡云凌/项目申报书_蔡云凌_对外汉语教育学院.pdf;', '工号0006172220，蔡云凌，女，出生年月为1972年9月，硕士学位，对外汉语教育学院，副教授。研究方向为对外汉语教学，发表《对美国中文学校汉语教师的师资培训》、《谈对外汉语教材的可操作性-对教材编写原则及课程规范化的思考》、《浅谈预科教育的特殊性、教学目标及教学内容——以北京大学留学生预科项目为例》等论文多篇，承担多项科研项目，出版教材十多部，曾赴韩国、日本、英国、美国的大学讲授中文，并在美国、新加坡、西班牙、意大利、比利时等地进行师资培训工作。\r\n通讯地址：北京大学对外汉语教育学院；\r\n联系电话：13601324524；\r\nE-mail:cylwt@pku.edu.cn', NULL, '项目申报', NULL, NULL, '/upload/xinsilu/researchEnroll/2023/outputZipFile/项目申报/0006172220-蔡云凌/“中文作为第二语言教学法”课程教学模式的创新和实践.zip', '对外汉语教育学院', NULL, '其他创新教育与教学实践,', ',,院系;,,院系;,,院系;,,院系', '否', '', '一般', '通过', '无', '2023SQYB004', '0', NULL, '副教授', '13601324524', 'cylwt@pku.edu.cn', NULL, NULL, NULL, NULL, NULL, NULL);
