INSERT INTO `research_enroll` VALUES (2296, '2023-01-03 14:20:51', '0062039910', '张庆芬', '教学新思路2.0', '/upload/xinsilu/researchEnroll/2023/项目申报/0062039910-张庆芬/项目申报书  张庆芬  人民医院.doc;/upload/xinsilu/researchEnroll/2023/项目申报/0062039910-张庆芬/项目申报书 张庆芬 人民医院.pdf;', '北京大学人民医院，工号03991，张庆芬，女，1984年09月25日出生，博士，就职于北京大学人民医院，副主任医师。主持国家自然科学基金、北大人民医院研究与发展基金等项目。参与完成多项全国范围的多中心研究项目，以第一作者身份在国内外杂志发表多篇文章。 参与《围术期疼痛多学科管理》、 《牛津麻醉学手册》等多部麻醉学书籍的编写及翻译工作。主要研究方向为围术期疼痛管理及器官保护，教学方向为超声引导区域阻滞技术的临床应用，多次组织区域阻滞学习班，并担任授课教师。通讯地址：西城区西直门南大街11号北京大学人民医院麻醉科，100044；联系方式：18810035916；littlevon@163.com', NULL, '项目申报', NULL, NULL, '/upload/xinsilu/researchEnroll/2023/outputZipFile/项目申报/0062039910-张庆芬/教学新思路2.0.zip', '人民医院', NULL, '智能数字化教材建设应用,', '0062027830,冯艺,人民医院;0062036030,安海燕,人民医院;0062038440,张冉,人民医院;,,院系', '否', '', '重点', '通过', '无', '2023SQZD005', '0', NULL, '副主任医师', '18810035916', 'littlevon@163.com', NULL, NULL, NULL, NULL, NULL, NULL);
