INSERT INTO `research_enroll` VALUES (2308, '2023-01-03 15:01:49', '0663164507', '洪晶', '基于5G的VR视频系统在眼科学课程教学中的应用场景研究 ', '/upload/xinsilu/researchEnroll/2023/项目申报/0663164507-洪晶/项目申报书-洪晶-北京大学第三医院.doc;/upload/xinsilu/researchEnroll/2023/项目申报/0663164507-洪晶/项目申报书-洪晶-北京大学第三医院.pdf;', '洪晶，工号：0663164507，主任医师、博士生导师，北京大学第三医院眼科中心主任，北京大学第三医院眼科角膜眼表疾病科主任，北京大学第三医院眼科眼库主任。在近10年时间每年进行医学部本科生授课工作，主要涵盖眼科学基础及视网膜病等理论授课，授课经验丰富，课堂气氛活跃，深受学生好评。2007年负责人在中国率先开展了自动角膜刀取材内皮移植手术（DSAEK）技术，随着手术技术的成熟及摸索出更适合中国人眼解剖结构的手术方法后，亲临全国30余家大型三甲及专科医院开展DSAEK手术，为全国培养50余名进修医生，连续八年组织《角膜内皮移植手术技术的应用》全国继续教育学习班，参与人次3万余人次。', NULL, '项目申报', NULL, NULL, '/upload/xinsilu/researchEnroll/2023/outputZipFile/项目申报/0663164507-洪晶/基于5G的VR视频系统在眼科学课程教学中的应用场景研究 .zip', '第三医院', NULL, '信息技术与智慧教学案例,', '0663181480,韩亮,第三医院;1463189468,江晓丹,第三医院;2163192485,岑羽捷,第三医院;1763190038,郭雨欣,第三医院', '否', '', '一般', '通过', '无', '2023SQYB024', '0', NULL, '主任医师', '13910573646', 'Hongjing1964@sina.com', NULL, NULL, NULL, NULL, NULL, NULL);
